            self.cursor.execute(
                """ SELECT note FROM clientes
                WHERE cod LIKE '%s' """% codigo0)

            tratamento = self.cursor.fetchall()

            for resultado in tratamento:
                resultado = str(resultado)
                resultado=resultado.replace("[", "")
                resultado=resultado.replace("]", "")
